import React from "react";
import styled from "styled-components";
import { AiOutlineLeft, AiOutlineRight } from "react-icons/ai";
import { BotonRedondo } from "../assets/styles/style";

export const Pagination = ({ totalPages, onPrev, onNext, pages }) => {
  console.log(pages, totalPages);
  return (
    <>
      {totalPages === 0 ? null : (
        <Container>
          {pages > 1 ? (
            <BotonRedondo onClick={onPrev}>
              <AiOutlineLeft />
            </BotonRedondo>
          ) : null}
          <h3>
            <span>
              {pages > totalPages / 2
                ? Math.ceil(pages / totalPages)
                : Math.floor(pages / totalPages) + 1}
            </span>{" "}
            - <span>{totalPages}</span>
          </h3>
          {pages === totalPages ? null : (
            <BotonRedondo onClick={onNext}>
              <AiOutlineRight />
            </BotonRedondo>
          )}
        </Container>
      )}
    </>
  );
};

const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  margin-right: 50px;
  align-items: center;
  justify-content: flex-end;
  gap: 10px;
  buttonBotonRedondo
  }
`;
