import React from "react";
import styled from "styled-components";
import { AiOutlineLeft, AiOutlineRight } from "react-icons/ai";
import { BotonRedondo } from "../assets/styles/style";
import { useSelector } from "react-redux";

export const Pagination = ({ totalPages, onPrev, onNext, pages }) => {
    const loading = useSelector(state=> state.loa)
  return (
    <>
      {totalPages === 0 ? null : (
        <Container>
          {pages > 1 ? (
            <BotonRedondo onClick={onPrev}>
              <AiOutlineLeft />
            </BotonRedondo>
          ) : null}
          {isNaN(pages) === 0 ? (
            <h3>Cargando Paginado</h3>
          ) : (
            <h3>
              <span>{pages}</span> - <span>{totalPages}</span>
            </h3>
          )}

          {pages === totalPages ? null : (
            <BotonRedondo onClick={onNext}>
              <AiOutlineRight />
            </BotonRedondo>
          )}
        </Container>
      )}
    </>
  );
};

const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  margin-right: 50px;
  align-items: center;
  justify-content: flex-end;
  gap: 10px;
  buttonBotonRedondo
  }
`;
