import React from "react";
import styled from "styled-components";
import Charizard from "../assets/image/charizard.png";

export const Card = ({pokemon}) => {
  return (
    <Container>
      <picture className="head_container">
        <img src={pokemon.image} alt={pokemon.name} />
      </picture>
      <div className="body_container">
        <div className="title">
          <h3>Charizard</h3>
          <h4>{`#00${pokemon.id}`}</h4>
        </div>
        <div className="types">
          {pokemon}
        </div>
      </div>
    </Container>
  );
};

const Container = styled.div`
  width: 200px;
  height: 300px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  border-radius: 15px;
  box-shadow: 0 5px 10px rgba(0, 0, 0, 0.7);
  transition: 0.5s ease-in-out;
  &:hover {
    transform: translateY(-20px);
  }
  .head_container {
    width: 100%;
    height: 120px;
    position: relative;

    img {
      width: 150px;
      position: absolute;
      left: 13%;
      top: -15%;
    }
  }
  .body_container {
    width: 100%;
    height: calc(100% - 120px);
    display: flex;
    flex-direction: column;
    align-items: center;
    .title {
      width: 100%;
      height: (100% - 80px);
      text-align: center;
      font-size: 1.3rem;
      text-transform: uppercase;
    }
    .types {
      width: 100%;
      height: 100%;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 20px;
      span {
        display: block;
        padding: 5px 10px;
        background-color: red;
        border-radius: 10px;
      }
    }
  }
`;
