import React from "react";
import styled from "styled-components";
import { AiOutlineLeft, AiOutlineRight } from "react-icons/ai";
import { BotonRedondo } from "../assets/styles/style";
import { useSelector } from "react-redux";

export const Pagination = ({ totalPages, onPrev, onNext, pages, loading }) => {

  return (
    <>
      {!totalPages? <h3 style={{width: "100%", height:"100%", textAlign: "center", fontSize:"18px", color:""}}>Cargando Paginado</h3> : (
        <Container>
          {pages > 1 ? (
            <BotonRedondo onClick={onPrev}>
              <AiOutlineLeft />
            </BotonRedondo>
          ) : null}
              <h3>
              <span>{pages}</span> - <span>{totalPages}</span>
            </h3>
          {pages === totalPages ? null : (
            <BotonRedondo onClick={onNext}>
              <AiOutlineRight />
            </BotonRedondo>
          )}
        </Container>
      )}
    </>
  );
};

const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  margin-right: 50px;
  align-items: center;
  justify-content: flex-end;
  gap: 10px;
  buttonBotonRedondo
  }
`;
