import React from 'react';
import styled from 'styled-components';

export const Pagination = ({totalPages, onPrev, onNext, pages}) =>{
return (
      <Container>
        <div className="pagination"> <button onClick={onPrev}>prev</button>
          <h3><span>{pages}</span> de <span>{totalPages}</span></h3>
          <button onClick={onNext}>next</button></div>

      </Container>
);
}

const Container = styled.div`
width: 100%;
height: 100%;
display: flex;
align-items: center;
justify-content: flex-end;
margin-right: 50px;
    .pagination{
        width: 100px;
        height: auto;
        display: flex;
        align-items: flex-end;
        gap: 20px;
    }
`;
