import React from "react";
import styled from "styled-components";
import TitleBanner from "../assets/image/titulon.png";
import { SearchBar } from "./index";
import { Link } from "react-router-dom";
/* icons */
import { MdFavorite, MdCreateNewFolder } from "react-icons/md";

export const NavBar = () => {
  return (
    <Container>
      <header>
        <img src={TitleBanner} alt="logo de la pagina" />
      </header>
      <nav className="icons">
        <ul>
          <li>
            <MdCreateNewFolder className="icon" />
            <span className="title">
              <Link to={"/favorite"} style={{ color: "#333" }}>
                Create Pokemon
              </Link>
            </span>
          </li>
          <li>
            <MdFavorite className="icon" />
            <span className="title">
              <Link to={"/createPokemon"} style={{ color: "#333" }}>
                My team
              </Link>
            </span>
          </li>
        </ul>
      </nav>
      <div className="search-container">
        <SearchBar />
      </div>
    </Container>
  );
};

const Container = styled.div`
  width: 90vw;
  height: 100px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  header {
    width: 200px;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    img {
      width: 200px;
    }
  }
  .search-container {
    width: 400px;
    height: 50px;
    border: 1px solid black;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-radius: 30px;
    overflow: hidden;
  }
  .icons {
    width: 450px;
    display: flex;
    align-items: center;
    ul {
      width: 100%;
      height: auto;
      position: relative;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 50px;

      li {
        position: relative;
        list-style: none;
        width: 60px;
        height: 60px;
        background: #fff;
        border-radius: 60px;
        cursor: pointer;
        display: flex;
        justify-content: center;
        align-items: center;
        box-shadow: 0px 10px 25px rgba(0, 0, 0, 0.1);
        transition: 0.5s;
        &:hover {
          width: 180px;
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0);
          &::before {
            opacity: 1;
          }
          &::after {
            opacity: 0.5;
          }
          .icon {
            transform: scale(0);
            color: #fff;
            transition-delay: 0.25s;
          }
          .title {
            transform: scale(1);
            transition-delay: 0.25s;
          }
        }
        &::before {
          content: "";
          position: absolute;
          inset: 0;
          border-radius: 60px;
          background: linear-gradient(45deg, red, white);
          opacity: 0;
          transition: 0.5s;
        }
        &::after {
          content: "";
          position: absolute;
          top: 10px;
          width: 100%;
          height: 100%;
          border-radius: 60px;
          background: linear-gradient(45deg, white, var);
          transition: 0.5s;
          filter: blur(15px);
          z-index: -1;
          opacity: 0;
        }
        .icon {
          color: #777;
          font-size: 1.75em;
          transition: 0.5s;
          transition-delay: 0.25s;
        }
        span {
          position: absolute;
        }
        .title {
          color: #fff;
          font-size: 1.1em;
          text-transform: uppercase;
          letter-spacing: 0.1em;
          transform: scale(0);
          transition: 0.5s;
          transition-delay: 0s;
        }
      }
    }
  }
`;
