import React, { useState } from "react";
import styled from "styled-components";
//Componentes
import { Pagination, FilterBar, CardContext } from "../components/index";
//Hooks
import { useSelector, useDispatch } from "react-redux";
//actions
import {
  updateOrder,
  sortOrder,
  updateType,
} from "../redux/actions";
//helpers
import { ordered, tipos } from "../assets/utils/utils.js";

export const HomePage = () => {
  const dispatch = useDispatch();
  //estados generales de redux
  const typeState = useSelector((state) => state.types);
  let pokemonState = useSelector((state) => state.pokemons);
  const type = useSelector((state) => state.type);
  const order = useSelector((state) => state.order);

  //helpers
  if (order) pokemonState = ordered(order, pokemonState);
  if (type) pokemonState = tipos(type, pokemonState);

  //estados locales, MANEJAN LA ACCION DEL TOOGLE, EL PAGINADO Y EL FILTRO DE TIPOS Y ORDEN
  //controla el toogle de la barra de filtros
  const [show, setShow] = useState(false);
  //controlador de filtros por tipo
  const [types, setTypes] = useState({
    normal: false,
    flying: false,
    ground: false,
    bug: false,
    steel: false,
    water: false,
    electric: false,
    ice: false,
    dark: false,
    unknown: false,
    fighting: false,
    poison: false,
    rock: false,
    ghost: false,
    fire: false,
    grass: false,
    psychic: false,
    dragon: false,
    fairy: false,
    shadow: false,
  });
  const [ordenar, setOrdenar] = useState({
    "a-z": false,
    "z-a": false,
    "fuerza+": false,
    "fuerza-": false,
    createdBDD: false,
    api: false,
  });
  const [unknownValue, setUnknownValue] = useState("unknown");
  const [unknownChecked, setUnknownChecked] = useState(true);
  const [selectedType, setSelectedType] = useState("unknown");

  // funciones de filtros
  const handleCheckbox = (e) => {
    const typeName = e.target.name;
    if (typeName === "unknown") {
      dispatch(updateType(""));
      setUnknownValue("unknown");
      setSelectedType("unknown");
      setUnknownChecked(true);
    } else {
      setSelectedType(typeName);
      dispatch(updateType(typeName));

      setUnknownValue("");
      setUnknownChecked(false);
    }
    setPage(1);
  };
  const handleOrder = (e) => {
    setOrdenar({
      ...ordenar,
      [e.target.name]: e.target.checked,
    });
    if (e.target.checked) {
      dispatch(sortOrder(e.target.value));
    } else {
      dispatch(updateOrder(""));
    }
  };
  //funciones de pagiado
  const itemsPerPage = 12;
  const totalPages = Math.ceil(pokemonState.length / itemsPerPage);

  const pagination = () => {
    const startIndex = (page - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    if (pokemonState.length) return pokemonState.slice(startIndex, endIndex);
  };

  const [page, setPage] = useState(1); // Inicialización de page en 1

  const onNextPage = () => {
    if (page < totalPages) {
      setPage((prevPage) => prevPage + 1);
    }
  };

  const onPreviusPage = () => {
    if (page > 1) {
      setPage((prevPage) => prevPage - 1);
    }
  };

  const onFirstPage = () => {
    setPage(1);
  };

  const onLastPage = () => {
    setPage(totalPages);
  };

  const pokePagination = pagination();

  return (
    <Container>
      <div className="pagination_and_filteres">
        <div className="top_content">
          <div className="filteres">
            <FilterBar openDrawwer={setShow} drawwer={show} />
          </div>
          <div className="pagination">
            <Pagination
              totalPages={totalPages}
              pages={page}
              onPrev={onPreviusPage}
              onNext={onNextPage}
              fastPrev={onFirstPage}
              fastNext={onLastPage}
            />
          </div>
        </div>
      </div>
      <div className={`${show === false ? "drawwer_close" : "drawwer"}`}>
        <div className="top_bar">
          <h3>Menu de filtros</h3>
          <button onClick={() => setShow(!show)}>X</button>
        </div>
        <hr />
        <div className="mid_bar">
          <div className="title">
            <h3>Filtros de tipo</h3>
          </div>
          <div className="checkbox_container">
            {typeState?.map((e) => (
              <div className="checkbox" key={e.id}>
                <input
                  type="radio"
                  value={e.name}
                  id={e.id}
                  name={e.name}
                  checked={e.name === selectedType}
                  onChange={handleCheckbox}
                />
                <label htmlFor={e.name}>{e.name}</label>
              </div>
            ))}
          </div>
        </div>
        <hr />
        <div className="bottom_bar">
          <div className="title">
            <h3>otros filtros</h3>
          </div>
          <div className="other_filteres">
            <div className="checkbox">
              <input
                type="checkbox"
                name="z-a"
                value="z-a"
                onClick={handleOrder}
              />
              <label>z - a</label>
            </div>
            <div className="checkbox">
              <input
                type="checkbox"
                name="a-z"
                value="a-z"
                onClick={handleOrder}
              />
              <label htmlFor="">a - z</label>
            </div>
            <div className="checkbox">
              <input
                type="checkbox"
                name="ataque+"
                value="ataque+"
                onClick={handleOrder}
              />
              <label htmlFor="">ataque+</label>
            </div>
            <div className="checkbox">
