    width: 100%;
    height: 120px;
    display: flex;
    align-items: center;
    justify-content: center;
    img {
      width: 150px;
      object-fit: cover;
    }
  }
  .body_container {
    margin-top: 20px;
    width: 100%;
    height: calc(100% - 120px);
