      .flying {
        background-color: #3dc7ef;
      }

      .ground {
        background-color: #ab9842;
      }

      .steel {
        background-color: #9eb7b8;
      }

      .psychic {
        background-color: #f366b9;
      }

      .ice {
        background-color: #51c4e7;
      }

      .ghost {
        background-color: #4d5b64;
      }

      .normal {
