export const GET_POKEMONS = "GET_POKEMONS";
export const GET_POKEMON = "GET_POKEMON";
export const CREATE_POKEMON = "CREATE_POKEMON";
export const SEARCH_POKEMON = "SEARCH_POKEMON";
export const GET_TYPES = "GET_TYPES";
export const FILTER_TYPES = "FILTER_TYPES";
export const SORT_ORDER = "SORT_ORDER";
export const UPDATE_POKEMONS = "UPDATE_POKEMONS";
export const UPDATE_POKEMON = "UPDATE_POKEMON";
export const UPDATE_ORDER = "UPDATE_ORDER";
export const UPDATE_TYPE = "UPDATE_TYPES";
export const UPDATE_SEARCH = ""

export const getPokemonFilter = (payload) => {
  return {
    type: GET_POKEMON_FILTERS,
    payload,
  };
};

export const updatePokemons = (payload) => {
  return {
    type: UPDATE_POKEMONS,
    payload,
  };
};

export const updateType = (payload) => {
  return {
    type: UPDATE_TYPE,
    payload,
  };
};

export const updateOrder = (payload) => {
  return {
    type: UPDATE_ORDER,
    payload,
  };
};

export function getTypes() {
  return function (dispatch) {
    return fetch("http://localhost:3001/types")
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_TYPES,
          payload: json,
        })
      );
  };
}

export function getPokemons() {
  return function (dispatch) {
    return fetch("http://localhost:3001/pokemons")
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_POKEMONS,
          payload: json,
        })
      );
  };
}

export function getPokemon(id) {
  return function (dispatch) {
    return fetch(`http://localhost:3001/pokemons/${id}`)
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_POKEMON,
          payload: json,
        })
      );
  };
}

export const searchPokemons =(payload)=>{
  return{
    type: SEARCH_POKEMON,
    payload,
  }
}

export function filterTypes(type) {
  return function (dispatch) {
    dispatch({
      type: FILTER_TYPES,
      payload: type,
    });
  };
}

export function sortOrder(order) {
  return function (dispatch) {
    dispatch({
      type: SORT_ORDER,
      payload: order,
    });
  };
}

export function updatePokemon(payload) {
  return {
    type: UPDATE_POKEMON,
    payload,
  };
}
