    z-index: 50;
    display: flex;
    flex-direction: column;
    align-items: center;
    transition: 0.5s ease-in;
    hr{
      width: 80%;
      background: #fff;
      border: 1px solid #fff;

    }
    .top_bar {
      background-color: red;
      width: 100%;
