      margin-bottom: 10px;
      h3 {
        width: 80%;
        text-align: center;
        color: #fff;
        text-transform: capitalize;
        text-decoration: underline;
        font-weight: 500;
        letter-spacing: 1px;
      }
      button {
        width: 35px;
        height: 35px;
        border-radius: 50%;
        border: 1px solid #fff;
        display: flex;
        align-items: center;
        justify-content: center;
        margin-right: 10px;
        background: none;
        transition: 0.3s ease;
        color: #fff;
        font-size: 1.1rem;
        font-weight: 500;
        &:hover {
          transform: scale(1.2);
          background-color: #333;
        }
      }
    }
    .mid_bar {

    }
  }
  .pagination_and_filteres {
    position: relative;
    width: 100%;
    height: 50px;
    display: flex;
    justify-content: space-evenly;
    flex-wrap: wrap;
    .top_content {
      background-color: red;
      width: 100%;
      height: 50px;
      display: flex;
      .filteres {
        width: 20%;
        height: 50px;
        display: flex;
        align-items: flex-start;
        justify-content: center;
      }
      .pagination {
        width: 80%;
        height: 50px;
        display: flex;
        align-items: center;
      }
    }
  }
  .card_context {
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;
