import styled from "styled-components";

export const BotonRedondo = styled.button`
  width: 100%;
  height: 100%;
  border-radius: 50%;
  border: 1px solid #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 10px;
  background: none;
  transition: 0.3s ease;
  color: #fff;
  font-size: 1.1rem;
  font-weight: 500;
  &:hover {
    transform: scale(1.2);
    background-color: #333;
  }
`;

export const BTNGoBack = styled.button`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  border: 1px solid ${((props)=>props.border === true? "#fff":"#111")};
  background: none;
  font-size: 1.1rem;
  transition: 0.3s ease;
  color: ${((props)=> props.color === true ? "#fff":"#111")}
  &:hover {
    transform: scale(1.1);
  }
`;

export const BotonRectangulo = styled.button``;
