import React, { useEffect } from "react";
import { Routes, Route, loca } from "react-router-dom";
import { useDispatch } from "react-redux";
import { NavBar } from "./components";
import {
  LandingPage,
  HomePage,
  CreatePage,
  DetailPage,
  Favorites,
  ErrorPage,
} from "./pages/index";
import { getTypes, getPokemons } from "./redux/actions";

function App() {
  //logica del componente
  const dispatch = useDispatch();
  //carga el estado inicial de tipos y los pokemones
  useEffect(() => {
    dispatch(getTypes());
    dispatch(getPokemons());
  }, []);
  const showNavbar = location.pathname === "/home";
  return (
    <Routes>
      <Route path="/" element={<LandingPage />} />
      <Route path="/home" element={<HomePage />} />
      <Route path="/createPokemon" element={<CreatePage />} />
      <Route path="/pokemon/:id" element={<DetailPage />} />
      <Route path="/favorites" element={<Favorites />} />
      <Route path="*" element={<ErrorPage />} />
      {showNavbar && <Navbar />}
    </Routes>
  );
}

export default App;
