import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";

export const PokeForm = () => {
  const dispatch = useDispatch();
  const option = useSelector((state) => state.types);
  const image1 = "https://pbs.twimg.com/media/FSuY3vRWQAIdW1n.jpg:large";
  const image2 =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT1UXzy0foT7zd4Mi-weWOxrep4WFfeukQi8TJdWygALA&s";
  const image3 =
    "https://www.carteltec.com/wp-content/uploads/2020/12/1608806108_624_Pokemon-Pikachu-imaginado-en-los-17-tipos-diferentes-de-Pokemon.jpeg";
  const image4 =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQqmqlPkNL3Qp_kwILIrudtjCUDl4yM9pVZvA&usqp=CAU";
  const image5 =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSaCZzJvBSd0xFBpWWAjJvN_x91g6YZHgvPIw&usqp=CAU";
  const pokeArrayImagenes = [image1, image2, image3, image4, image5];

  const [mostrarImagen, setMostrarImagen] = useState(
    "https://cdn.pixabay.com/photo/2013/07/13/12/07/avatar-159236_640.png"
  );
  const [imagenSeleccionada, setImagenSeleccionada] = useState(null);

  const [selectTipo1, setSelectTipo1] = useState("");
  const [selectTipo2, setSelectTipo2] = useState("");

  const validate = (input) => {
    let errors = {};

    if (!input.name) {
      errors.name = "El name es obligatorio";
    }

    return errors;
  };

  const [data, setData] = useState({
    name: "",
    image:null,
    vida: 0,
    fuerza: 0,
    defensa: 0,
    velocidad: 0,
    altura: 0,
    peso: 0,
    tipos: [],
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    if (e.target.name !== "name") {
      setData({
        ...data,
        [e.target.name]: Number(e.target.value) <= 0 ? 0 : e.target.value,
      });
    } else {
      setErrors(
        validate({
          ...data,
          [e.target.name]: e.target.value,
        })
      );
      setData({
        ...data,
        [e.target.name]: e.target.value,
      });
    }
  };

  const checkbox = (e) => {
    if (data.tipos.includes(e.target.value)) {
      data.tipos = data.tipos.filter((id) => id !== e.target.value);
      setData({
        ...data,
        tipos: data.tipos,
      });
    } else {
      setData({
        ...data,
        tipos: [...data.tipos, e.target.value],
      });
    }
  };

  const submit = async (e) => {
    e.preventDefault();
    const crear = await fetch("https://kevindex.herokuapp.com/pokemons", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
    const respuesta = await crear.json();
    console.log(respuesta);
    setData({
      name: "",
      vida: 0,
      fuerza: 0,
      defensa: 0,
      velocidad: 0,
      altura: 0,
      peso: 0,
      tipos: [],
    });
  };

  return (
    <Container>
      <div className="card_container">
        <div className="img_box">
          <img src={mostrarImagen} alt="imagen de muestra" />
        </div>
        <div className="card_context">
          <h3>
