import React from "react";
import styled from "styled-components";
import Charizard from "../assets/image/charizard.png";

export const Card = () => {
  return (
    <Container>
      <picture className="head_container">
        <img src={Charizard} alt="" />
      </picture>
    </Container>
  );
};

const Container = styled.div`
  width: 200px;
  height: 300px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;

  border-radius: 15px;

  box-shadow: 0 5px 10px rgba(0, 0, 0, 0.7);
  .head_container{
    width: 100%;
    height: 150px;
    position: relative;
    img{
        width: 150px;
        position: absolute;
        left: 13%;
        top: -10%;
        &:hover{
            transform: translateY()
        }
    }
  }
`;
