import React from "react";
import styled from "styled-components";
import { AiOutlineLeft, AiOutlineRight } from "react-icons/ai";
import { BotonRedondo } from "../assets/styles/style";


export const Pagination = ({ totalPages, onPrev, onNext, pages, state, pokeState }) => {

  const fastFowardLeft = ()=>{
    state(0)
  }

  const fastFowardRigth = ()=>{
    const finalPage = pokeState.length ? pokeState.length - 12 : false;
  }

  return (
    <>
      <Container>
        {!totalPages ? (
          <h3>Cargando Paginado..</h3>
        ) : (
          <>
            {/* CREANDO BOTTON FAST FOWARD */}
            <BotonRedondo onClick={()=>fastFowardLeft()}>
              <AiOutlineLeft/>
              <AiOutlineLeft/>
            </BotonRedondo>
            {pages > 1 ? (
              <BotonRedondo onClick={onPrev}>
                <AiOutlineLeft />
              </BotonRedondo>
            ) : null}
            <h3>
              <span>{pages}</span> de <span>{totalPages}</span>
            </h3>
            {pages === totalPages ? null : (
              <BotonRedondo onClick={onNext}>
                <AiOutlineRight />
              </BotonRedondo>
            )}
            <BotonRedondo>
              <AiOutlineRight onClick={()=>fastFowardRigth()}/>
              <AiOutlineRight/>
            </BotonRedondo>
          </>
        )}
      </Container>
    </>
  );
};

const Container = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  margin-right: 50px;
  align-items: center;
  justify-content: flex-end;
  gap: 10px;
  transition: all 0.3s;
  h3 {
    transition: all 0.3s;
    font-size: 18px;
    letter-spacing: 1px;

  }
`;
