import React, { useState } from "react";
import styled from "styled-components";
import {
  NavBar,
  Pagination,
  FilterBar,
  CardContext,
} from "../components/index";
import { useSelector, useDispatch } from "react-redux";
import {
  filterTypes,
  updateOrder,
  sortOrder,
  updateType,
} from "../redux/actions";
import { ordered, tipos } from "../assets/utils/utils.js";

export const HomePage = () => {
  /* ESTADOS GENERALES */
  const dispatch = useDispatch();
  const typeState = useSelector((state) => state.types);
  let pokemonState = useSelector((state) => state.pokemons);
  const type = useSelector((state) => state.type);
  const order = useSelector((state) => state.order);

  //FILTROS
  if (order) pokemonState = ordered(order, pokemonState);
  if (type) pokemonState = tipos(type, pokemonState);

  //ESTADOS GENERALES, MANEJAN LA ACCION DEL TOOGLE, EL PAGINADO Y EL FILTRO DE TIPOS
  //controla el toogle de la barra de filtros
  const [show, setShow] = useState(false);
  //controlador de filtros por tipo
  const [types, setTypes] = useState({
    normal: false,
    flying: false,
    ground: false,
    bug: false,
    steel: false,
    water: false,
    electric: false,
    ice: false,
    dark: false,
    unknown: false,
    fighting: false,
    poison: false,
    rock: false,
    ghost: false,
    fire: false,
    grass: false,
    psychic: false,
    dragon: false,
    fairy: false,
    shadow: false,
  });
  const [ordenar, setOrdenar] = useState({
    "a-z": false,
    "z-a": false,
    "fuerza+": false,
    "fuerza-": false,
    createdBDD: false,
    api: false,
  });
  // funciones de filtros
  const handleCheckbox = (e) => {
    setTypes({
      ...types,
      [e.target.name]: e.target.checked,
    });
    if (e.target.checked) {
      dispatch(filterTypes(e.target.name));
      setPage(0);
    } else {
      dispatch(updateType(""));
    }
  };
  const handleOrder = (e) => {
    setOrdenar({
      ...ordenar,
      [e.target.name]: e.target.checked,
    });
    if (e.target.checked) {
      dispatch(sortOrder(e.target.value));
    } else {
      dispatch(updateOrder(""));
    }
  };
  //funciones de pagiado
  const [page, setPage] = useState(0);
  const itemsPerPage = 12;
  const totalPages = Math.ceil(pokemonState.length / itemsPerPage);

  const pagination = () => {
    const startIndex = page * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return pokemonState.slice(startIndex, endIndex);
  };

  const pokePagination = pagination();

  const onNextPage = () => {
    if (page < totalPages - 1) {
      setPage(page + 1);
    }
  };

  const onPreviusPage = () => {
    if (page > 0) {
      setPage(page - 1);
    }
  };
  /* const pagination = () => {
    if (pokemonState.length) return pokemonState.slice(page, page + 12);
    return [];
  };
  const pokePagination = pagination();
  const totalPages = pokemonState.length
    ? Math.floor(pokemonState.length / 12) + 1
    : null;
  let currentPage =
    totalPages === 13
      ? Math.ceil(page / totalPages) + 1
      : Math.ceil(page / pokemonState.length) + 1;
  const onNextPage = () => {
    if (pokemonState.length > page + 12) {
      setPage(page + 12);
    }
  };
  const onPreviusPage = () => {
    if (page > 0) {
      setPage(page - 12);
    }
  }; */

  return (
    <Container>
      <NavBar />
      <div className="pagination_and_filteres">
        <div className="top_content">
          <div className="filteres">
            <FilterBar openDrawwer={setShow} drawwer={show} />
          </div>
          <div className="pagination">
            <Pagination
              totalPages={totalPages}
              pages={page}
              onPrev={onPreviusPage}
              onNext={onNextPage}
            />
          </div>
        </div>
      </div>
      <div className={`${show === false ? "drawwer_close" : "drawwer"}`}>
        <div className="top_bar">
          <h3>Menu de filtros</h3>
          <button onClick={() => setShow(!show)}>X</button>
        </div>
        <hr />
        <div className="mid_bar">
          <div className="title">
            <h3>Filtros de tipo</h3>
          </div>
          <div className="checkbox_container">
            {typeState?.map((e) => (
              <div className="checkbox" key={e.id}>
                <input
                  type="checkbox"
                  value={e.name}
                  id={e.id}
                  name={e.name}
                  onClick={handleCheckbox}
                />
                <label htmlFor={e.name}>{e.name}</label>
              </div>
            ))}
          </div>
        </div>
        <hr />
        <div className="bottom_bar">
          <div className="title">
            <h3>otros filtros</h3>
          </div>
          <div className="other_filteres">
            <div className="checkbox">
              <input
                type="checkbox"
                name="z-a"
                value="z-a"
                onClick={handleOrder}
              />
              <label>z - a</label>
            </div>
            <div className="checkbox">
              <input
                type="checkbox"
                name="a-z"
                value="a-z"
