import {
  GET_POKEMON,
  GET_POKEMONS,
  SEARCH_POKEMON,
  GET_TYPES,
  SORT_ORDER,
  FILTER_TYPES,
  UPDATE_POKEMON,
} from "./actions";

const initialState = {
  types: [],
  pokemons: [],
  pokemonFiltered: [],
  pokemon: {},
  isLoading: true,
  type: [],
};

export const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_TYPES:
      return {
        ...state,
        types: action.payload,
        isLoading: false,
      };
    case GET_POKEMONS:
      return {
        ...state,
        pokemons: action.payload,
        isLoading: false,
      };
    case GET_POKEMON:
      return {
        ...state,
        pokemon: action.payload,
        isLoading: false,
      };
    case UPDATE_POKEMON:
      return {
        ...state,
        pokemon: action.payload,
        isLoading: true,
      };
    case SEARCH_POKEMON:
      return {
        ...state,
        pokemons: action.payload,
        isLoading: false,
      };
    case FILTER_TYPES:
      return {
        ...state,
        type: state.type.concat(action.payload),
        pokemon: state.pokemons.filter((e) =>
          e.tipos.name
            ? e.tipos.name.includes(state.type)
            : e.tipos.includes(state.type)
        ),
        isLoading: false,
      };

    default:
      return state;
  }
};
