export const GET_POKEMONS = "GET_POKEMONS";
export const GET_POKEMON = "GET_POKEMON";
export const CREATE_POKEMON = "CREATE_POKEMON";
export const SEARCH_POKEMON = "SEARCH_POKEMON";
export const GET_TYPES = "GET_TYPES";
export const SORT_ORDER = "SORT_ORDER";
export const FILTER_TYPES = "FILTER_ TYPES";
export const MY_TEAM = "MY_TEAM";

export function getTypes() {
  return function (dispatch) {
    const URL = "http://localhost:3001/types";
    return fetch(URL)
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_TYPES,
          payload: json,
        })
      );
  };
}

export function getPokemons() {
  return function (dispatch) {
    return fetch("http://localhost:3001/pokemons")
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_POKEMONS,
          payload: json,
        })
      );
  };
}

export function getPokemon(id) {
  return function (dispatch) {
    return fetch(`http://localhost:3001/pokemons/${id}`)
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_POKEMON,
          payload: json,
        })
      );
  };
}

export function searchPokemon(name, id) {
  return function (dispatch) {
    if (name) {
      return fetch(`http://localhost:3001/pokemons/?name=${name}`)
        .then((res) => res.json())
        .then((json) =>
          dispatch({
            type: SEARCH_POKEMON,
            payload: json,
          })
        );
    } else
      return fetch(`http://localhost:3001/pokemons/${id}`)
        .then((res) => res.json())
        .then((json) =>
          dispatch({
            type: SEARCH_POKEMON,
            payload: json,
          })
        );
  };
}

export const filterTypes = (type) => {
  dispatch({
    type: FILTER_TYPES,
    payload: type,
  });
};
export const sortOrder = (order) => {
  dispatch({
    type: SORT_ORDER,
    payload: order,
  });
};
