export const PokeForm = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const option = useSelector((state) => state.types);
  const image1 =
    "https://raw.githubusercontent.com/MartaFagundez/pokedex-frontend/main/src/images/pk1.png";
  const image2 =
    "https://raw.githubusercontent.com/MartaFagundez/pokedex-frontend/main/src/images/pk2.png";
  const image3 =
    "https://raw.githubusercontent.com/MartaFagundez/pokedex-frontend/main/src/images/pk3.png";
  const image4 =
    "https://raw.githubusercontent.com/MartaFagundez/pokedex-frontend/main/src/images/pk4.png";
  const image5 =
    "https://raw.githubusercontent.com/MartaFagundez/pokedex-frontend/main/src/images/pk5.png";

  const [mostrarImagen, setMostrarImagen] = useState(
    "https://cdn.pixabay.com/photo/2013/07/13/12/07/avatar-159236_640.png"
  );

  const [selectTipo1, setSelectTipo1] = useState(null);
  const [selectTipo2, setSelectTipo2] = useState(null);

  const [data, setData] = useState({
    name: "",
    image: null,
    vida: 0,
    fuerza: 0,
    defensa: 0,
    velocidad: 0,
    altura: 0,
    peso: 0,
    tipos: [],
  });

  const [errors, setErrors] = useState({});

  const validate = (input) => {
    let errors = {};
    if (!input.name) {
      errors.name = "El name es obligatorio";
    }
    return errors;
  };

  const handleErrors = (input) => {
    const error = {};
    if (!data.name) {
      setErrors((error.name = "el nombre es requerido"));
    }
    if (!data.tipos.length == 0) {
      setErrors((error.tipos = "deve selleccionar al menos un tipo"));
    }
  };

  const handleSelectImage = (e) => {
    setMostrarImagen(e.target.value);
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
  };
  const handleSelectedType1 = (e) => {
    const { value } = e.target;
    setSelectTipo1(value);
    setData({
      ...data,
      tipos: [value],
    });
  };
  const handleSelectedType2 = (e) => {
    const { value } = e.target;
    setSelectTipo2(value);
    setData({
      ...data,
      tipos: [...data.tipos, value],
    });
  };
  const handleInput = (e) => {
    e.preventDefault();
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
  };
  const typeSelector = (value) => {
    switch (value) {
      case value === 1:
        return "normal";
      case value === 2:
        return "fighting";
      case value === 3:
        return "flying";
      case value === 4:
        return "poison";
      case value === 5:
        return "ground";
      case value === 6:
        return "rock";
      case value === 7:
        return "bug";
      case value === 8:
        return "ghost";
      case value === 9:
        return "steel";
      case value === 10:
        return "fire";
      case value === 11:
        return "water";
      case value === 12:
        return "grass";
      case value === 13:
        return "electric";
      case value === 14:
        return "psychic";
      case value === 15:
        return "ice";
      case value === 16:
        return "dragon";
      case value === 17:
        return "dark";
      case value === 18:
        return "fairy";
      case value === 19:
        return "unknown";
      case value === 20:
        return "shadow";
      default:
        return value;
    }
  };
  const type1 = typeSelector(selectTipo1);
  const type2 = typeSelector(selectTipo2);
  console.log(type)

  const handleSubmit = async (e) => {
    e.preventDefault();

    const crear = await fetch("http://localhost:3001/pokemons", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
    dispatch(getPokemons());
    const respuesta = await crear.json();
    setData({
      name: "",
      vida: 0,
      fuerza: 0,
      defensa: 0,
      velocidad: 0,
      altura: 0,
      peso: 0,
      tipos: [],
    });
    alert("pokemon creado con exito");
    navigate(-1);
  };

  return (
    <Container>
      <div className="card_container">
        <div className="img_box">
          <img src={mostrarImagen} alt="imagen de muestra" />
        </div>
        <div className="card_context">
          <h3>
            Vida: <span>{data.vida}</span>
          </h3>
          <h3>
            fuerza: <span>{data.fuerza}</span>
          </h3>
          <h3>
            defensa: <span>{data.defensa}</span>
          </h3>
          <h3>
            velocidad: <span>{data.velocidad}</span>
          </h3>
          <h3>
            peso: <span>{data.peso}</span>
          </h3>
          <h3>
            altura: <span>{data.altura}</span>
          </h3>
        </div>
        <div className="type_content">
          <span>{typeSelector(selectTipo1)}</span>
          <span>{typeSelector(selectTipo2)}</span>
        </div>
      </div>
      <div className="form_container_values">
        <div className="selected_image">
          <h3>seleccione una image</h3>
          <select name="image" onChange={handleSelectImage}>
            <option value="">seleccion de imagenes</option>
            <option value={image1}>avatar 1</option>
            <option value={image2}>avatar 2</option>
            <option value={image3}>avatar 3</option>
            <option value={image4}>avatar 4</option>
            <option value={image5}>avatar 5</option>
          </select>
        </div>
        <form action="POST" className="form_container" onSubmit={handleSubmit}>
          <div className="name_container">
            <label>
              Nombre
              <input
                type="text"
                placeholder="ingresa el nombre de tu pokemon"
                name="name"
                value={data.name}
                onChange={handleInput}
                required
              />
            </label>
            <p className={errors.name ? "danger" : "safe"}>{errors.name}</p>
          </div>

          <div className="container_type_selector">
            <div className="type_selector">
              <label>
                1º Tipo
                <select
                  defaultValue="default"
                  name="tipo1"
                  value={
                    data.tipos.length >= 1 ? data.tipos.length[0] : selectTipo1
                  }
                  onChange={handleSelectedType1}
                  required
                >
                  <option disabled selected value="default">
                    Tipo 1
                  </option>
                  {option.map((e) => (
                    <option key={e.id} value={e.id}>
                      {e.name}
                    </option>
                  ))}
                </select>
              </label>
            </div>
            <div className="type_selector">
              {selectTipo1 ? (
                <label>
                  2º Tipo
                  <select
                    onChange={handleSelectedType2}
                    defaultValue="default"
                    name="tipo2"
                    value={
                      data.tipos.length === 2
                        ? data.tipos.length[1]
                        : selectTipo2
                    }
                  >
                    <option disabled selected={!selectTipo2} value="default">
                      Tipo 2
                    </option>
                    {option
                      .filter((e) => e.id !== selectTipo1)
                      .map(({ name, id }) => (
                        <option value={id} key={id}>
                          {name}
                        </option>
                      ))}
                  </select>
                </label>
              ) : null}
            </div>
          </div>
          <div className="container_points_range">
            <label>
              vida
              <input
                onChange={handleInput}
                name="vida"
                defaultValue={data.vida}
                type="range"
                min="1"
                max="100"
              />
            </label>
            <label>
              fuerza
              <input
                onChange={handleInput}
                name="fuerza"
                defaultValue={data.fuerza}
                type="range"
                min="1"
                max="100"
              />
            </label>
            <label>
              defensa
              <input
                onChange={handleInput}
                name="defensa"
                defaultValue={data.defensa}
                type="range"
                min="1"
                max="100"
              />
            </label>
            <label>
              velocidad
              <input
                onChange={handleInput}
                name="velocidad"
                defaultValue={data.velocidad}
                type="range"
                min="1"
                max="100"
              />
            </label>
            <label>
              peso
              <input
                name="peso"
                defaultValue={data.peso}
                onChange={handleInput}
                type="range"
                min="1"
                max="100"
              />
            </label>
            <label>
              altura
              <input
                name="altura"
                defaultValue={data.altura}
                onChange={handleInput}
                type="range"
                min="1"
                max="100"
              />
            </label>
          </div>
          <div className="btnCrear">
            <button>Crear</button>
          </div>
        </form>
      </div>
    </Container>
  );
};

