import {
  GET_POKEMON,
  GET_POKEMONS,
  SEARCH_POKEMON,
  GET_TYPES,
  SORT_ORDER,
  FILTER_TYPES,
} from "./actions";

const initialState = {
  types: [],
  pokemons: [],
  pokemon: {},
  isLoading: true,
  totalPage: Math.floor(pokemons.length / 12),
  currentPage: 1,
};

export const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_TYPES:
      return {
        ...state,
        types: action.payload,
        isLoading: false,
      };
    case GET_POKEMONS:
      return {
        ...state,
        pokemons: action.payload,
        isLoading: false,
      };
    case GET_POKEMON:
      return {
        ...state,
        pokemon: action.payload,
        isLoading: false,
      };
    case SEARCH_POKEMON:
      return {
        ...state,
        pokemons: action.payload,
        isLoading: false,
      };
    case FILTER_TYPES:
      return {
        ...state,
        pokemons: pokemons.filter((pokemon) =>
          pokemon.type.includes(action.paylad)
        ),
        isLoading: false,
      };
    case SORT_ORDER:
      return {
        ...state,
        pokemons: action.paylad
          ? pokemons.sor((a, b) => b.name.charCodeAt() - a.name.charCodeAt())
          : pokemons.sor((a, b) => a.name.charCodeAt() - b.name.charCodeAt()),
          isL
      };
    default:
      return state;
  }
};
