import React from "react";
import styled from "styled-components";
import TitleBanner from "../assets/image/titulon.png";
import { SearchBar } from "./index";
import { Link } from "react-router-dom";

export const NavBar = () => {
  return (
    <Container>
      <header>
        <img src={TitleBanner} alt="logo de la pagina" />
      </header>
      <nav className="icons">
        <ul>
          <li>
            <HomeIcon className="icon" />
            <span className="title">
              <Link to={"/favorite"} style={{ color: "#333" }}>
                Home
              </Link>
            </span>
          </li>
          <li>
            <FavoriteIcon className="icon" />
            <span className="title">
              <Link to={"/favorite"} style={{ color: "#333" }}>
                Favorite {`(${favorite.length})`}
              </Link>
            </span>
          </li>
        </ul>
      </nav>
      <div className="search-container">
        <SearchBar />
      </div>
    </Container>
  );
};

const Container = styled.div`
  width: 90vw;
  height: 100px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  header {
    width: 250px;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    img {
      width: 200px;
    }
  }
  .search-container {
    width: 300px;
    height: 50px;
    border: 1px solid black;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-radius: 30px;
    overflow: hidden;
  }
  .container-nav {
    width: 400px;
    height: auto;
    ul {
      width: 100%;
      height: auto;
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 30px;
      list-style: none;
    }
    li {
        span{
            display: block;
            padding: 10px 20px;

            border-top: 1px solid black;
        }
    }
  }
`;
