import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";

export const PokeForm = () => {
  const dispatch = useDispatch();
  const option = useSelector((state) => state.types);
  const image1 = "https://pbs.twimg.com/media/FSuY3vRWQAIdW1n.jpg:large";
  const image2 =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT1UXzy0foT7zd4Mi-weWOxrep4WFfeukQi8TJdWygALA&s";
  const image3 =
    "https://www.carteltec.com/wp-content/uploads/2020/12/1608806108_624_Pokemon-Pikachu-imaginado-en-los-17-tipos-diferentes-de-Pokemon.jpeg";
  const image4 =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQqmqlPkNL3Qp_kwILIrudtjCUDl4yM9pVZvA&usqp=CAU";
  const image5 =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSaCZzJvBSd0xFBpWWAjJvN_x91g6YZHgvPIw&usqp=CAU";
  const pokeArrayImagenes = [image1, image2, image3, image4, image5];

  const [mostrarImagen, setMostrarImagen] = useState(
    "https://cdn.pixabay.com/photo/2013/07/13/12/07/avatar-159236_640.png"
  );
  const [imagenSeleccionada, setImagenSeleccionada] = useState(null);

  const [selectTipo1, setSelectTipo1] = useState("");
  const [selectTipo2, setSelectTipo2] = useState("");

  const validate = (input) => {
    let errors = {};

    if (!input.name) {
      errors.name = "El name es obligatorio";
    }

    return errors;
  };

  const [data, setData] = useState({
    name: "",
    vida: 0,
    fuerza: 0,
    defensa: 0,
    velocidad: 0,
    altura: 0,
    peso: 0,
    tipos: [],
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    if (e.target.name !== "name") {
      setData({
        ...data,
        [e.target.name]: Number(e.target.value) <= 0 ? 0 : e.target.value,
      });
    } else {
      setErrors(
        validate({
          ...data,
          [e.target.name]: e.target.value,
        })
      );
      setData({
        ...data,
        [e.target.name]: e.target.value,
      });
    }
  };

  const checkbox = (e) => {
    if (data.tipos.includes(e.target.value)) {
      data.tipos = data.tipos.filter((id) => id !== e.target.value);
      setData({
        ...data,
        tipos: data.tipos,
      });
    } else {
      setData({
        ...data,
        tipos: [...data.tipos, e.target.value],
      });
    }
  };

  const submit = async (e) => {
    e.preventDefault();
    const crear = await fetch("https://kevindex.herokuapp.com/pokemons", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
    const respuesta = await crear.json();
    console.log(respuesta);
    setData({
      name: "",
      vida: 0,
      fuerza: 0,
      defensa: 0,
      velocidad: 0,
      altura: 0,
      peso: 0,
      tipos: [],
    });
  };

  return (
    <Container>
      <div className="selected_image">
        <h3>seleccione una image</h3>

        <img src={mostrarImagen} alt="imagen de muestra" />
        <select>
          <options value="default">avatars</options>
          <options value="image1">avatar 1</options>
          <options value="image2">avatar 2</options>
          <options value="image3">avatar 3</options>
          <options value="image4">avatar 4</options>
          <options value="image5">avatar 5</options>
        </select>
      </div>
      <form action="POST" className="form_container">
        //nombre del pokemon
        <div className="name_container">
          <label>
            Nombre
            <input type="text" />
          </label>
        </div>
        //selectores de tipo
        <div className="container_type_selector">
          <div className="type_selector">
            <label>
              1º Tipo
              <select name="" id="">
                <options value="default">Tipo 1</options>
                {option.map((e, index) => (
                  <options key={index}>e</options>
                ))}
              </select>
            </label>
          </div>
          <div className="type_selector">
            <label>
              2º Tipo
              <select name="" id="">
                <options value="default">Tipo 2</options>
                {option
                  .filter((e) => e.name !== selectTipo1)
                  .map((e, id) => (
                    <options key={id}>{e}</options>
                  ))}
              </select>
            </label>
          </div>
        </div>
        <div className="container_points_range">
          <label>
            vida
            <input type="range" min="1" max="100" name="" />
          </label>
          <label>
            fuerza
            <input type="range" min="1" max="100" />
          </label>
          <label>
            defensa
            <input type="range" min="1" max="100" />
          </label>
          <label>
            velocidad
            <input type="range" min="1" max="100" />
          </label>
          <label>
            peso
            <input type="range" min="1" max="100" />
          </label>
          <label>
            altura
            <input type="range" min="1" max="100" />
          </label>
        </div>
      </form>
    </Container>
  );
};

const Container = styled.div`
  width: 100%;
  height: 500px;
  display: flex;
  .selected_image {
    width: 80%;
    height: 150px;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    margin-top: 10px;
    position: relative;
    gap: 30px;
    h3 {
      margin-left: 180px;
      color: #fff;
      text-transform: uppercase;
      text-decoration: underline;
      font-weight: 400;
    }
    select {
      width: 100px;
      height: 40px;
      color: #fff;
      background: rgba(0, 0, 0, 0.3);
      backdrop-filter: blur(5px);
      border: none;
      border-radius: 10px;
      font-size: 18px;
      font-weight: 300;
      text-align: center;
    }
    img {
      position: absolute;
      top: 0;
      left: 0;
      width: 150px;
      height: 150px;
      object-fit: cover;
      border-radius: 50%;
    }
  }
  .form_container {
    width: 80%;
    height: auto;
    display: flex;
    align-items: flex-start;
    justify-content: flex-start;
  }
`;
