import { type } from "@testing-library/user-event/dist/type";
import {
  GET_POKEMONS,
  GET_POKEMON,
  CREATE_POKEMON,
  SEARCH_POKEMON,
  GET_TYPES,
  FILTER_TYPES,
  SORT_ORDER,
  UPDATE_POKEMONS,
  UPDATE_POKEMON,
  UPDATE_ORDER,
  UPDATE_TYPE,
  UPDATE_SEARCH,
} from "./actionTypes";

//FUNCIONES DE LIMPIEZA

export const updateSearch = (payload) => {
  return {
    type: UPDATE_SEARCH,
    payload,
  };
};

export const updatePokemons = (payload) => {
  return {
    type: UPDATE_POKEMONS,
    payload,
  };
};
export const updatePokemon = (payload) => {
  return {
    type: UPDATE_POKEMON,
    payload,
  };
};

export const updateType = (payload) => {
  return {
    type: UPDATE_TYPE,
    payload,
  };
};

export const updateOrder = (payload) => {
  return {
    type: UPDATE_ORDER,
    payload,
  };
};

//LLAMADOS A LA API

export function getTypes() {
  return function (dispatch) {
    return fetch("http://localhost:3001/types")
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_TYPES,
          payload: json,
        })
      );
  };
}

export function getPokemons() {
  return function (dispatch) {
    return fetch("http://localhost:3001/pokemons")
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_POKEMONS,
          payload: json,
        })
      );
  };
}

export function getPokemon(id) {
  return function (dispatch) {
    return fetch(`http://localhost:3001/pokemons/${id}`)
      .then((res) => res.json())
      .then((json) =>
        dispatch({
          type: GET_POKEMON,
          payload: json,
        })
      );
  };
}
export const searchPokemon = (name) => async (dispatch) => {
  try {
    const response = await fetch(`http://localhost:3001/pokemons?name=${name}`);
    const data = await response.json();
    if(response.status !== 200) {
      return dispatch({
        type: SEARCH_POKEMON,
        payload: data,
      })
    }
    dispatch({
      type: SEARCH_POKEMON,
      payload: data,
    })
  } catch (error) {
   throw error;
  }
};
