} */

export function filterTypes(type) {
  return function (dispatch) {
    dispatch({
      type: FILTER_TYPES,
      payload: type,
    });
  };
}

export function sortOrder(order) {
  return function (dispatch) {
    dispatch({
      type: SORT_ORDER,
      payload: order,
    });
  };
}
