import React from "react";
import styled from "styled-components";
import Charizard from "../assets/image/charizard.png";

export const Card = () => {
  return (
    <Container>
      <picture className="head_container">
        <img src={Charizard} alt="" />
      </picture>
      <div className="body_container">
        <div className="title">
            <h3>Charizard</h3>
        </div>
      </div>
    </Container>
  );
};

const Container = styled.div`
  width: 200px;
  height: 300px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;

  border-radius: 15px;

  box-shadow: 0 5px 10px rgba(0, 0, 0, 0.7);
  .head_container{
    width: 100%;
    height: 120px;
    position: relative;
    background:red;
    img{
        width: 150px;
        position: absolute;
        left: 13%;
        top: -15%;
        &:hover{
        }
    }
  }
  .body_container{
    width: 100%;
    height: calc(100% - 120px);
    display
  }
`;
