import React, { useState } from "react";
import styled from "styled-components";
import {
  NavBar,
  Pagination,
  FilterBar,
  CardContext,
} from "../components/index";
import { useSelector, useDispatch } from "react-redux";
import {
  filterTypes,
  getPokemons,
  updateType,
  getPokemonFilter,
  updatePokemons,
} from "../redux/actions";
import { tipos, ordered } from "../assets/utils/utils.js";

export const HomePage = () => {
  /* ESTADOS GENERALES */
  const dispatch = useDispatch();
  const typeState = useSelector((state) => state.types);
  let pokemonState = useSelector((state) => state.pokemons);
  const type = useSelector((state) => state.type);
  const order = useSelector((state) => state.order);

  //FILTROS                            
  if (type) pokemonState = tipos(type, pokemonState);
  if (order) pokemonState = ordered(order, pokemonState);
  //ESTADOS GENERALES, MANEJAN LA ACCION DEL TOOGLE, EL PAGINADO Y EL FILTRO DE TIPOS
  //controla el toogle de la barra de filtros
  const [show, setShow] = useState(false);
  //controla el paginado // intntar realizar un custom hook
  const [page, setPage] = useState(0);
  //contra el eventro de filtros del checkbox;
  const [types, setTypes] = useState({
    normal: false,
    flying: false,
    ground: false,
    bug: false,
    steel: false,
    water: false,
    electric: false,
    ice: false,
    dark: false,
    unknown: false,
    fighting: false,
    poison: false,
    rock: false,
    ghost: false,
    fire: false,
    grass: false,
    psychic: false,
    dragon: false,
    fairy: false,
    shadow: false,
  });
  /* FUNCIONES DE FILTROS */
  const handleCheckbox = (e) => {
    setTypes({
      ...types,
      [e.target.name]: e.target.checked,
    });
    if (e.target.checked) {
      dispatch(filterTypes(e.target.name));
    } else {
      dispatch(updateType(""));
    }
  };

  //CORTE DEL ARRAY ESTADO GENERAL QUE GUARDA LOS POKEMONS
  const pagination = () => {
    if(pokemonState.length) return pokemonState.slice(page, page + 12)
    return []
  };
  //ARRAY CON EL QUE TENGO QUE TRABAJAR
  const pokePagination = pagination();
  //TOTAL DE PAGINAS
  const totalPages = Math.ceil(pokemonState.length / 12);
  //PAGINA ACTUAL
  let currentPage = Math.ceil(page / totalPages) + 1;
  //CONTROLADOR DE EVENTO NE
  const onNextPage = () => {
    if (pokemonState.length > page + 12) {
      setPage(page + 12);
    }
  };
  //CONTROLADOR DE EVENTO PREV
  const onPreviusPage = () => {
    if (page > 0) {
      setPage(page - 12);
    }
  };
  return (
    <Container>
      <NavBar />
      <div className="pagination_and_filteres">
        <div className="top_content">
          <div className="filteres">
            <FilterBar openDrawwer={setShow} drawwer={show} />
          </div>
          <div className="pagination">
            <Pagination
              totalPages={totalPages}
              pages={currentPage}
              onPrev={onPreviusPage}
              onNext={onNextPage}
            />
          </div>
        </div>
      </div>
      <div className={`${show === false ? "drawwer_close" : "drawwer"}`}>
        <div className="top_bar">
          <h3>Menu de filtros</h3>
          <button onClick={() => setShow(!show)}>X</button>
        </div>
        <hr />
        <div className="mid_bar">
          <div className="title">
            <h3>Filtros de tipo</h3>
          </div>
          <div className="checkbox_container">
            {typeState?.map((e) => (
              <div className="checkbox" key={e.id}>
                <input
                  type="checkbox"
                  value={e.name}
                  id={e.id}
                  name={e.name}
                  onClick={handleCheckbox}
                />
                <label htmlFor={e.name}>{e.name}</label>
              </div>
            ))}
          </div>
        </div>
        <hr />
        <div className="bottom_bar">
          <div className="title">
            <h3>otros filtros</h3>
          </div>
          <div className="other_filteres">
            <div className="checkbox">
              <input type="checkbox" />
              <label>z - a</label>
            </div>
            <div className="checkbox">
              <input type="checkbox" />
              <label>ataque +</label>
            </div>
            <div className="checkbox">
              <input type="checkbox" />
              <label>ataque -</label>
            </div>
          </div>
        </div>
      </div>
      <div className="card_context">
        <CardContext pokePagination={pokePagination} />
      </div>
    </Container>
  );
};

